export async function getAllBeliefs(options?: {
  type?: BeliefType;
  status?: string;
  minConfidence?: number;
  limit?: number;
}): Promise<Belief[]> {
  const conditions: string[] = [];
  const params: (string | number)[] = [];
  let paramCount = 0;

  if (options?.type) {
    paramCount++;
    conditions.push(`belief_type = $${paramCount}`);
    params.push(options.type);
  }

  if (options?.status) {
    paramCount++;
    conditions.push(`status = $${paramCount}`);
    params.push(options.status);
  } else {
    conditions.push(`status = 'active'`);
  }

  if (options?.minConfidence) {
    paramCount++;
    conditions.push(`confidence >= $${paramCount}`);
    params.push(options.minConfidence);
  }

  const whereClause = conditions.length > 0
    ? `WHERE ${conditions.join(' AND ')}`
    : '';

  const limit = options?.limit || 100;
  paramCount++;
  params.push(limit);

  const result = await pool.query<Belief>(
    `SELECT * FROM beliefs
     ${whereClause}
     ORDER BY confidence DESC, reinforcement_count DESC
     LIMIT $${paramCount}`,
    params
  );
  return result.rows;
}

