     JOIN insight_sources s ON s.insight_id = i.id
     WHERE s.source_type = $1 AND s.source_id = $2 AND i.status = 'active'
     ORDER BY i.priority DESC, i.confidence DESC`,
    [sourceType, sourceId]
  );
