export async function getNeighborhoodForDate(
  datePatterns: string[],
  options: TraversalOptions = {},
  eventDate?: Date // Optional normalized date to search event_date column
): Promise<GraphNode[]> {
  if (datePatterns.length === 0 && !eventDate) return [];

  const opts = { ...DEFAULT_OPTIONS, ...options };
  const nodes: GraphNode[] = [];
  const seenIds = new Set<string>();

  // 1. Query by event_date column if provided
  if (eventDate) {
    const eventDateResult = await pool.query<Memory>(
      `SELECT id, content, source, created_at, salience_score, event_date
       FROM memories
       WHERE event_date = $1
         AND salience_score >= $2
       ORDER BY salience_score DESC
       LIMIT $3`,
      [eventDate, opts.minSalience, Math.floor(opts.maxNodes / 2)]
    );

    for (const row of eventDateResult.rows) {
      if (!seenIds.has(row.id)) {
        seenIds.add(row.id);
        nodes.push({
          id: row.id,
          kind: 'memory',
          content: row.content,
          created_at: row.created_at,
          score: (row.salience_score / 10) * 1.2, // Boost exact date matches
          salience: row.salience_score,
          source: row.source,
        });
      }
    }
  }

  // 2. Query by content text patterns (fallback/supplement)
  if (datePatterns.length > 0 && nodes.length < opts.maxNodes) {
    const likeClauses = datePatterns.map((_, i) => `content ILIKE $${i + 1}`).join(' OR ');
    const likeParams = datePatterns.map((p) => `%${p}%`);

    const textResult = await pool.query<Memory>(
      `SELECT id, content, source, created_at, salience_score
       FROM memories
       WHERE (${likeClauses})
         AND salience_score >= $${datePatterns.length + 1}
       ORDER BY salience_score DESC, created_at ASC
       LIMIT $${datePatterns.length + 2}`,
      [...likeParams, opts.minSalience, opts.maxNodes - nodes.length]
    );

    for (const row of textResult.rows) {
      if (!seenIds.has(row.id)) {
        seenIds.add(row.id);
        nodes.push({
          id: row.id,
          kind: 'memory',
          content: row.content,
          created_at: row.created_at,
          score: row.salience_score / 10,
          salience: row.salience_score,
          source: row.source,
        });
      }
    }
  }

  return nodes.sort((a, b) => b.score - a.score);
}

export async function getNeighborhoodForPerson(
  personName: string,
  options: TraversalOptions = {}
): Promise<GraphNode[]> {
  const { maxNodes, minSalience } = { ...DEFAULT_OPTIONS, ...options };

  const result = await pool.query<Memory>(
    `SELECT DISTINCT m.id, m.content, m.source, m.created_at, m.salience_score
     FROM memories m
     JOIN entity_mentions em ON em.memory_id = m.id
     JOIN entities e ON e.id = em.entity_id
     WHERE (e.name ILIKE $1 OR e.canonical_name ILIKE $1 OR $1 = ANY(e.aliases))
       AND e.entity_type = 'person'
       AND m.salience_score >= $2
     ORDER BY m.salience_score DESC, m.created_at DESC
     LIMIT $3`,
    [`%${personName}%`, minSalience, maxNodes]
  );

  return result.rows.map((row) => ({
    id: row.id,
    kind: 'memory' as const,
    content: row.content,
    created_at: row.created_at,
    score: row.salience_score / 10,
    salience: row.salience_score,
    source: row.source,
  }));
}

