export async function getMemoryGraphStats(): Promise<{
  totalMemories: number;
  similarEdges: number;
  entityMentions: number;
  summaryLinks: number;
  avgEdgesPerMemory: number;
}> {
  const result = await pool.query(`
    SELECT
      (SELECT COUNT(*) FROM memories) as total_memories,
      (SELECT COUNT(*) FROM memory_edges WHERE edge_type = 'SIMILAR') as similar_edges,
      (SELECT COUNT(*) FROM entity_mentions) as entity_mentions,
      (SELECT COUNT(*) FROM memory_summary_links) as summary_links
  `);

  const row = result.rows[0];
  const totalMemories = parseInt(row.total_memories, 10);
  const similarEdges = parseInt(row.similar_edges, 10);

  return {
    totalMemories,
    similarEdges,
    entityMentions: parseInt(row.entity_mentions, 10),
    summaryLinks: parseInt(row.summary_links, 10),
    avgEdgesPerMemory: totalMemories > 0 ? (similarEdges * 2) / totalMemories : 0,
  };
}

