export async function sendToSubscription(
  subscription: PushSubscription,
  payload: PushPayload
): Promise<SendResult> {
  ensureVapidConfigured();

  if (!isPushConfigured()) {
    return {
      subscription_id: subscription.id,
      success: false,
      error: 'VAPID keys not configured',
    };
  }

  const pushSubscription = {
    endpoint: subscription.endpoint,
    keys: {
      p256dh: subscription.p256dh,
      auth: subscription.auth,
    },
  };

  try {
    const response = await webpush.sendNotification(
      pushSubscription,
      JSON.stringify(payload)
    );

    // Update last_used_at on success
    await pool.query(
      `UPDATE push_subscriptions
       SET last_used_at = NOW(),
           failure_count = 0,
           updated_at = NOW()
       WHERE id = $1`,
      [subscription.id]
    );

    return {
      subscription_id: subscription.id,
      success: true,
      statusCode: response.statusCode,
    };
  } catch (error) {
    const err = error as Error & { statusCode?: number };
    const statusCode = err.statusCode;
    const errorMessage = err.message || 'Unknown error';

    // Update failure tracking
    await pool.query(
      `UPDATE push_subscriptions
       SET failure_count = failure_count + 1,
           last_failure_at = NOW(),
           last_failure_reason = $1,
           updated_at = NOW()
       WHERE id = $2`,
      [errorMessage, subscription.id]
    );

    // Check if we should deactivate
    // 410 Gone means the subscription is no longer valid
    // 404 means endpoint not found
    if (statusCode === 410 || statusCode === 404) {
      await deactivateSubscription(subscription.id);
    } else {
      // Check if we've exceeded max failures
      const updated = await getSubscription(subscription.id);
      if (updated && updated.failure_count >= MAX_FAILURE_COUNT) {
        await deactivateSubscription(subscription.id);
      }
    }

    return {
      subscription_id: subscription.id,
      success: false,
      error: errorMessage,
      statusCode,
    };
  }
}

