export async function getTierStats(): Promise<{
  hypothesis: number;
  solid: number;
  avgHypothesisConfidence: number;
  avgSolidConfidence: number;
}> {
  const result = await pool.query<{
    tier: string;
    count: string;
    avg_confidence: string;
  }>(
    `SELECT tier, COUNT(*) as count, AVG(confidence) as avg_confidence
     FROM memories
     WHERE tier IS NOT NULL
     GROUP BY tier`
  );

  const stats = {
    hypothesis: 0,
    solid: 0,
    avgHypothesisConfidence: 0,
    avgSolidConfidence: 0,
  };

  for (const row of result.rows) {
    if (row.tier === 'hypothesis') {
      stats.hypothesis = parseInt(row.count, 10);
      stats.avgHypothesisConfidence = parseFloat(row.avg_confidence);
    } else if (row.tier === 'solid') {
      stats.solid = parseInt(row.count, 10);
      stats.avgSolidConfidence = parseFloat(row.avg_confidence);
    }
  }

  return stats;
}

