export async function getAllGaps(options?: {
  type?: GapType;
  status?: GapStatus;
  priority?: GapPriority;
  entityId?: string;
  minSeverity?: number;
  limit?: number;
}): Promise<KnowledgeGap[]> {
  const conditions: string[] = [];
  const params: (string | number)[] = [];
  let paramCount = 0;

  if (options?.type) {
    paramCount++;
    conditions.push(`gap_type = $${paramCount}`);
    params.push(options.type);
  }

  if (options?.status) {
    paramCount++;
    conditions.push(`status = $${paramCount}`);
    params.push(options.status);
  } else {
    conditions.push(`status = 'open'`);
  }

  if (options?.priority) {
    paramCount++;
    conditions.push(`priority = $${paramCount}`);
    params.push(options.priority);
  }

  if (options?.entityId) {
    paramCount++;
    conditions.push(`(related_entity_id = $${paramCount} OR secondary_entity_id = $${paramCount})`);
    params.push(options.entityId);
  }

  if (options?.minSeverity !== undefined) {
    paramCount++;
    conditions.push(`severity >= $${paramCount}`);
    params.push(options.minSeverity);
  }

  const whereClause = conditions.length > 0
    ? `WHERE ${conditions.join(' AND ')}`
    : '';

  const limit = options?.limit || 100;
  paramCount++;
  params.push(limit);

  const result = await pool.query<KnowledgeGap>(
    `SELECT * FROM knowledge_gaps
     ${whereClause}
     ORDER BY
       CASE priority
         WHEN 'critical' THEN 1
         WHEN 'high' THEN 2
         WHEN 'medium' THEN 3
         WHEN 'low' THEN 4
       END,
       severity DESC,
       times_surfaced DESC
     LIMIT $${paramCount}`,
    params
  );
  return result.rows;
}

