export async function getAllQuestions(options?: {
  type?: QuestionType;
  status?: QuestionStatus;
  priority?: GapPriority;
  gapId?: string;
  entityId?: string;
  timingHint?: TimingHint;
  limit?: number;
}): Promise<ResearchQuestion[]> {
  const conditions: string[] = [];
  const params: (string | number)[] = [];
  let paramCount = 0;

  if (options?.type) {
    paramCount++;
    conditions.push(`question_type = $${paramCount}`);
    params.push(options.type);
  }

  if (options?.status) {
    paramCount++;
    conditions.push(`status = $${paramCount}`);
    params.push(options.status);
  } else {
    conditions.push(`status = 'pending'`);
  }

  if (options?.priority) {
    paramCount++;
    conditions.push(`priority = $${paramCount}`);
    params.push(options.priority);
  }

  if (options?.gapId) {
    paramCount++;
    conditions.push(`gap_id = $${paramCount}`);
    params.push(options.gapId);
  }

  if (options?.entityId) {
    paramCount++;
    conditions.push(`related_entity_id = $${paramCount}`);
    params.push(options.entityId);
  }

  if (options?.timingHint) {
    paramCount++;
    conditions.push(`timing_hint = $${paramCount}`);
    params.push(options.timingHint);
  }

  const whereClause = conditions.length > 0
    ? `WHERE ${conditions.join(' AND ')}`
    : '';

  const limit = options?.limit || 100;
  paramCount++;
  params.push(limit);

  const result = await pool.query<ResearchQuestion>(
    `SELECT * FROM research_questions
     ${whereClause}
     ORDER BY
       CASE priority
         WHEN 'critical' THEN 1
         WHEN 'high' THEN 2
         WHEN 'medium' THEN 3
         WHEN 'low' THEN 4
       END,
       created_at DESC
     LIMIT $${paramCount}`,
    params
  );
  return result.rows;
}

