): Promise<Session | null> {
  let query = `UPDATE sessions SET consolidation_status = $2`;
  const params: (string | Record<string, unknown>)[] = [sessionId, status];
  let paramIndex = 3;

  if (status === 'completed') {
    query += `, consolidated_at = NOW()`;
