function serializeIntent(intent: StoryIntent): string {
  switch (intent.kind) {
    case 'none':
      return 'none';
    case 'date_meaning':
      return `date:${intent.dateText.toLowerCase()}`;
    case 'origin_story':
      return `origin:${(intent.topic ?? '').toLowerCase()}`;
    case 'relationship_story':
      return `relationship:${(intent.personName ?? '').toLowerCase()}`;
    case 'self_story':
      return 'self';
    default:
      return 'unknown';
  }
}

