export function cacheStory(
  query: string,
  intent: StoryIntent,
  result: StoryResult,
  ttlMs: number = CACHE_TTL_MS
): void {
  const key = generateCacheKey(query, intent);
  const now = Date.now();

  // Enforce max cache size (LRU eviction)
  if (cache.size >= MAX_CACHE_SIZE) {
    evictLeastRecentlyUsed();
  }

  cache.set(key, {
    result,
    createdAt: now,
    expiresAt: now + ttlMs,
    hitCount: 0,
    intentKind: intent.kind,
  });

  stats.size = cache.size;
  console.log(`[StoryCache] Cached story for intent ${intent.kind} (TTL: ${ttlMs / 1000}s)`);
}

file:src/services/storyCache.ts:fn:evict.source:
function evictLeastRecentlyUsed(): void {
  let oldestKey: string | null = null;
  let oldestTime = Infinity;

  for (const [key, entry] of cache) {
    if (entry.createdAt < oldestTime) {
      oldestTime = entry.createdAt;
      oldestKey = key;
    }
  }

  if (oldestKey) {
    cache.delete(oldestKey);
    console.log('[StoryCache] Evicted oldest entry (LRU)');
  }
}

