function getCurrentTime(args: GetCurrentTimeArgs): string {
  const now = new Date();
  const format = args.format ?? 'full';

  const baseOptions: Intl.DateTimeFormatOptions = {
    timeZone: config.timezone,
  };

  let options: Intl.DateTimeFormatOptions;

  switch (format) {
    case 'date':
      options = {
        ...baseOptions,
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
      };
      break;

    case 'time':
      options = {
        ...baseOptions,
        hour: 'numeric',
        minute: '2-digit',
        second: '2-digit',
        timeZoneName: 'short',
      };
      break;

    case 'full':
    default:
      options = {
        ...baseOptions,
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: 'numeric',
        minute: '2-digit',
        timeZoneName: 'short',
      };
      break;
  }

  return now.toLocaleString('en-US', options);
}

