export async function detectConflict(
  calendar: GoogleCalendar,
  eventId: string,
  localEtag: string
): Promise<boolean> {
  const authClient = await getAuthenticatedClient(calendar.google_account_id);
  const calendarApi = google.calendar({ version: 'v3', auth: authClient });

  try {
    const response = await calendarApi.events.get({
      calendarId: calendar.calendar_id,
      eventId: eventId,
    });

    // If etags differ, there's a conflict
    return response.data.etag !== localEtag;
  } catch (err: unknown) {
    if (err instanceof Error && 'code' in err && (err as { code: number }).code === 404) {
      // Event was deleted on Google
      return true;
    }
    throw err;
  }
}

