export function HeaderBar({ onMenuToggle, isSideNavOpen }: HeaderBarProps) {
  // Real WebSocket connection status (P6-T6)
  const { isConnected, latency } = useWebSocket();

  return (
    <header className="h-14 bg-background-secondary border-b border-glass-border flex items-center justify-between px-4 shrink-0">
      {/* Left: Menu button (mobile) + Logo */}
      <div className="flex items-center gap-3">
        {/* Mobile menu toggle */}
        <button
          onClick={onMenuToggle}
          className="lg:hidden p-2 rounded-md hover:bg-background-tertiary transition-colors"
          aria-label={isSideNavOpen ? 'Close menu' : 'Open menu'}
        >
          <svg
            className="w-5 h-5 text-foreground"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            {isSideNavOpen ? (
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M6 18L18 6M6 6l12 12"
              />
            ) : (
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M4 6h16M4 12h16M4 18h16"
              />
            )}
          </svg>
        </button>

        {/* Logo */}
        <div className="flex items-center gap-2">
          <span className="text-xl font-bold text-primary glow-text-primary">
            Squire
          </span>
        </div>
      </div>

      {/* Center: Reserved for breadcrumb/title */}
      <div className="hidden md:flex items-center">
        {/* Will be populated by page context later */}
      </div>

      {/* Right: Status + Profile */}
      <div className="flex items-center gap-4">
        {/* WebSocket connection status (P6-T6) */}
        <div
          className={`flex items-center gap-2 px-2 py-1 rounded-md ${
            isConnected ? '' : 'bg-error/20 border border-error/50'
          }`}
        >
          <div
            className={`w-2 h-2 rounded-full ${
              isConnected
                ? 'bg-success animate-pulse'
                : 'bg-error animate-pulse'
            }`}
            title={isConnected ? `Connected${latency ? ` (${latency}ms)` : ''}` : 'Disconnected - messages may not be saved'}
          />
          <span className={`text-xs ${isConnected ? 'hidden sm:inline text-foreground-muted' : 'text-error font-medium'}`}>
            {isConnected ? (
              <>Live{latency ? <span className="text-foreground-subtle ml-1">({latency}ms)</span> : ''}</>
            ) : (
              'Offline - Refresh'
            )}
          </span>
        </div>

