          )}
          {list.list_type !== 'checklist' && (
            <span className="text-xs text-foreground-muted">
              {itemCount} {itemCount === 1 ? 'item' : 'items'}
            </span>
          )}
        </div>
      </div>
    </motion.button>
  );
}

