function DistanceLOD({ threshold, near, far }: DistanceLODProps) {
  const groupRef = useRef<THREE.Group>(null);
  const nearRef = useRef<THREE.Group>(null);
  const farRef = useRef<THREE.Group>(null);
  const { camera } = useThree();

  useFrame(() => {
    if (!groupRef.current || !nearRef.current || !farRef.current) return;
    groupRef.current.getWorldPosition(_worldPos);
    const distance = camera.position.distanceTo(_worldPos);
    const shouldBeNear = distance < threshold;

    // Directly set visibility on THREE.js objects - no React re-render!
    nearRef.current.visible = shouldBeNear;
    farRef.current.visible = !shouldBeNear;
  });

  return (
    <group ref={groupRef}>
      <group ref={nearRef}>{near}</group>
      <group ref={farRef} visible={false}>{far}</group>
    </group>
  );
}

function GLTFModel({
  buildingType,
  scale = 1,
  emissiveIntensity = 0,
  emissiveColor = '#ffffff',
  castShadow = true,
  receiveShadow = true,
}: BuildingModelProps) {
  const config = getModelConfig(buildingType);
  const { scene } = useGLTF(config.path);
  const clonedSceneRef = useRef<THREE.Object3D | null>(null);
  const materialsRef = useRef<THREE.MeshStandardMaterial[]>([]);

  // Clone scene once and keep stable reference
  if (!clonedSceneRef.current) {
    const clone = scene.clone(true);
    const materials: THREE.MeshStandardMaterial[] = [];

    clone.traverse((child) => {
      if (child instanceof THREE.Mesh && child.material) {
        const material = child.material.clone();
        if (material instanceof THREE.MeshStandardMaterial) {
          materials.push(material);
        }
        child.material = material;
        child.castShadow = castShadow;
        child.receiveShadow = receiveShadow;
      }
    });

    materialsRef.current = materials;
    clonedSceneRef.current = clone;
  }

  // Update emissive properties imperatively each render
  const color = new THREE.Color(emissiveColor);
  materialsRef.current.forEach((material) => {
    material.emissive = color;
    material.emissiveIntensity = emissiveIntensity;
  });

  // Apply config transforms
  const finalScale = scale * config.scale;

  return (
    <primitive
      object={clonedSceneRef.current}
      scale={[finalScale, finalScale, finalScale]}
      rotation={[0, config.rotationY, 0]}
      position={[0, config.yOffset, 0]}
    />
  );
}

function FallbackBox({
  scale = 1,
  emissiveIntensity = 0,
  emissiveColor = '#ffffff',
}: Pick<BuildingModelProps, 'scale' | 'emissiveIntensity' | 'emissiveColor'>) {
  return (
    <mesh castShadow receiveShadow scale={[scale, scale, scale]}>
      <boxGeometry args={[0.8, 1.0, 0.8]} />
      <meshStandardMaterial
        color="#4a5568"
        emissive={emissiveColor}
        emissiveIntensity={emissiveIntensity}
        roughness={0.7}
        metalness={0.2}
      />
    </mesh>
  );
}

export function BuildingModelWithSuspense(props: BuildingModelProps) {
  return (
    <Suspense
      fallback={
        <FallbackBox
          scale={props.scale}
          emissiveIntensity={props.emissiveIntensity}
          emissiveColor={props.emissiveColor}
        />
      }
    >
      <GLTFModel {...props} />
    </Suspense>
  );
}

export function BuildingModelWithFallback(props: BuildingModelProps) {
  // React error boundaries don't work well with Suspense in R3F
  // Instead, useGLTF will throw and Suspense will catch during load
  // For actual load failures, the model just won't render
  // A more robust solution would use react-error-boundary
  return <BuildingModel {...props} />;
}

