        />
      </EffectComposer>
    );
  }

  if (enableDOF) {
    return (
      <EffectComposer>
        <Bloom
          intensity={bloomIntensity}
          kernelSize={KernelSize.MEDIUM}
          luminanceThreshold={0.5}
          luminanceSmoothing={0.8}
          resolutionScale={0.5}
        />
        <Vignette
          offset={0.3}
          darkness={vignetteDarkness}
          blendFunction={BlendFunction.NORMAL}
        />
        <ChromaticAberration
          offset={new THREE.Vector2(chromaticOffset, chromaticOffset)}
          blendFunction={BlendFunction.NORMAL}
          radialModulation={true}
          modulationOffset={0.5}
        />
        <DepthOfField
          focusDistance={0.02}
          focalLength={0.05}
          bokehScale={3}
        />
      </EffectComposer>
    );
  }

  if (enableNoise) {
    return (
      <EffectComposer>
        <Bloom
          intensity={bloomIntensity}
          kernelSize={KernelSize.MEDIUM}
          luminanceThreshold={0.5}
          luminanceSmoothing={0.8}
          resolutionScale={0.5}
        />
        <Vignette
          offset={0.3}
          darkness={vignetteDarkness}
          blendFunction={BlendFunction.NORMAL}
        />
        <ChromaticAberration
          offset={new THREE.Vector2(chromaticOffset, chromaticOffset)}
          blendFunction={BlendFunction.NORMAL}
          radialModulation={true}
          modulationOffset={0.5}
        />
        <Noise
          opacity={0.08}
          blendFunction={BlendFunction.SOFT_LIGHT}
        />
      </EffectComposer>
    );
  }

  // Base effects only
  return (
    <EffectComposer>
      <Bloom
        intensity={bloomIntensity}
        kernelSize={KernelSize.LARGE}
